import { GET_ERROR } from '../actions/types';

const initState = {
    isAuthenticated: false,
    user: {}
};

export default function(state = initState, action) {
    switch(action.type) {
        case GET_ERROR:
            return action.payload;
        default: 
            return state;
    }
};
