import { SET_CURRENT_USER } from '../actions/types';

const initState = {
    isAuthenticated: false,
    user: {}
};

export default function(state = initState, action) {
    switch(action.type) {
        case SET_CURRENT_USER:
            return {
                user: action.payload.decoded
            }
        default: 
            return state;
    }
};
