import { GET_ERROR, LOGIN } from '../actions/auth';

const initState = {
    isAuthenticated: false,
    user: {}
};

export default function(state = initState, action) {
    switch(action.type) {
        case GET_ERROR:
            return {
                ...state,
            };
        case LOGIN:
            return {
                ...state,
                user: action.payload
            };
        default: 
            return state;
    }
};
